import React from 'react';
import { useNavigate } from 'react-router-dom';
import './EventView.scss';

const events = [
  {
    title: "Sommernacht Open Air",
    description: "Musik, Tanz und Streetfood unter freiem Himmel.",
    location: "Seepromenade Zürich",
    dateFrom: "2025-07-12T18:00:00",
    dateTo: "2025-07-12T23:30:00",
    imageBase64: "data:image/png;base64,",
    supportedBy: "Stadt Zürich",
  },
  {
    title: "Tech Conference Bern",
    description: "Networking, spannende Talks und Startup-Pitches.",
    location: "Kursaal Bern",
    dateFrom: "2025-09-01T09:00:00",
    dateTo: "2025-09-02T17:00:00",
    imageBase64: "data:image/png;base64,",
    supportedBy: "InnoSuisse",
  }
];

const EventView = () => {
  const navigate = useNavigate();

  const handleClick = (title) => {
    // Weiterleitung mit dem Event-Titel als URL-Parameter
    navigate(`/event/${encodeURIComponent(title)}`);
  };

  return (
    <div className="event-view-container">
      <h1>Events</h1>
      <div className="event-grid">
        {events.map((event, index) => (
          <div
            key={index}
            className="event-card"
            onClick={() => handleClick(event.title)}
          >
            <img
              src={`data:image/jpeg;base64,${event.imageBase64}`}
              alt={event.title}
              className="event-image"
            />
            <h3 className="event-title">{event.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export { events }; // Exportiere die events, damit Detail sie nutzen kann
export default EventView;
